import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import styled from "styled-components";
import CommentList from "../list/CommentList";
import TextInput from "../ui/TextInput";
import Button from "../ui/Button";
import Bbutton from "../ui/BackButton";
import Template from "../ui/template";
import BTemplate from "../ui/BackgroundBox";
import PTemplate from "../ui/PostBox";
import Likes from "../ui/likes";
import UserImage from "../ui/woman.png";
import ProgressBar from "../ui/ProgressBar";
import axios from "axios";
import Commenta from "../list/posts";
import Drop from "../ui/Drop";
import Userimg from "../ui/Userimg";

const MainTitleText = styled.p`
  font-size: 50px;
  font-weight: 900;
  text-align: center;
  color: #ffffff;
`;

const Wrapper = styled.div`
  padding: 16px;
  width: calc(100% - 32px);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const Container = styled.div`
  width: 100%;
  max-width: 720px;
  & > * {
    :not(:last-child) {
      margin-bottom: 16px;
    }
  }
`;
const TopContainer = styled.div`
  padding: 0px;
  border: transper;
  border-radius: 8px;
  background-color: #f9f9f9;
  display: flex;
  flexdirection: row;
  #userimage {
    width: 42px;
    height: 40px;
    left: 20%;
    right: 0%;
    top: 20%;
    bottom: 0%;
  }
`;

const PostContainer = styled.div`
  padding: 8px 16px;
  border: transper;
  border-radius: 8px;
  background: #f9f9f9;
`;
const LikesContainer = styled.div`
  padding: 0px;
  border: transper;
  border-radius: 8px;
  background: #f9f9f9;
  margin: 0 auto;
  display: block;
`;
const TitleText = styled.p`
  font-size: 17px;
  font-weight: 600;
  font-family: "GmarketSansTTFMedium";
  line-height: 23px;
  color: #000000;
  text-align: left;
  padding-top: 5px;
  padding-bottom: 10px;
`;

const ContentText = styled.p`
  font-style: normal;
  font-size: 13px;
  font-weight: 500;
  font-family: "GmarketSansTTFMedium";
  text-align: left;
  line-height: 1.5;
  white-space: pre-wrap;
  color: #000000;
`;

const CommentLabel = styled.p`
  padding-top: 15px;
  padding-left: 15px;
  padding-bottom: 8px;
  font-weight: 600;
  font-size: 15px;
  font-family: "GmarketSansTTFMedium";
  line-height: 18px;
  color: #000000;
`;
const Space = styled.div`
  width: 15px;
  height: 10px;
  display: inline-block;
`;
const ProgressContainer = styled.div`
  padding-top: 15px;
`;

const TestPage = () => {
  const [data, setData] = useState([]);
  const navigate = useNavigate();
  const [comment, setComment] = useState("");
  useEffect(() => {
    axios
      .get("http://35.216.20.124:8080/api/post/inquire?id=1")
      .then((Response) => {
        setData(Response.data);
        console.log(Response.data);
      })
      .catch((Error) => {
        console.log(Error);
      });
  }, []);

  return (
    <>
      {data.length}
      {data && (
        <Template>
          <Drop />
          <Userimg
            onClick={() => {
              navigate("/mypage");
            }}
          />
          <BTemplate>
            <Space></Space>
            <Container>
              <Button
                title="살까 말까"
                onClick={() => {
                  navigate("/post-write");
                }}
              />
              <Space></Space>
              <Button
                title="할까 말까"
                onClick={() => {
                  navigate("/post-write");
                }}
              />
              <Space></Space>
              <Button
                title="갈까 말까"
                onClick={() => {
                  navigate("/post-write");
                }}
              />
              <PTemplate>
                <TopContainer>
                  <Bbutton
                    icon="fa-solid fa-arrow-left"
                    onClick={() => {
                      navigate("/main-page");
                    }}
                  />
                  <img id="userimage" src={UserImage} />
                </TopContainer>

                <PostContainer>
                  <TitleText>{data.what} 살까 말까 </TitleText>
                  <ContentText>{data.content}</ContentText>
                </PostContainer>

                <LikesContainer>
                  <Likes />
                </LikesContainer>

                <ProgressContainer>
                  <ProgressBar done="70" />
                </ProgressContainer>

                <CommentLabel>댓글</CommentLabel>
                <Commenta></Commenta>

                <TextInput
                  height={50}
                  value={comment}
                  onChange={(event) => {
                    setComment(event.target.value);
                  }}
                />

                <Button
                  title="댓글 작성하기"
                  onClick={() => {
                    navigate("/post/:postId");
                  }}
                />
              </PTemplate>
            </Container>
          </BTemplate>
        </Template>
      )}
    </>
  );
};

export default TestPage;
