  width: 300px;
  height: 20px;
  flex-direction: row;

  padding-top: 10px;
  padding-right: 16px;
  padding-bottom: 30px;
  padding-left: 16px;
`;
const Label = styled.p`
  font-size: 16px;
  font-weight: 500;
  color: white;
`;

