import React, { useState } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  width: 300px;
  height: 20px;
  flex-direction: row;

  padding-top: 10px;
  padding-right: 0px;
  padding-bottom: 30px;
  padding-left: 5px;

  margin-left: 0;

`;
const Space = styled.div`
  width: 20px;
  height: auto;
  display: inline-block;
`;
const Compoment = styled.p`
  
  color: #000000;
`
function Likes() {
  const [thumbup, setThumbUp] = useState(0); //좋아요 버튼구현
  const [thumbdown, setThumbDown] = useState(0); //좋아요 버튼구현
  const [msg, setMsg] = useState(0); //좋아요 버튼구현
  const [star, setStar] = useState(0); //좋아요 버튼구현

  return (
    <Wrapper>
      <Compoment
        onClick={() => {
            setThumbUp(thumbup + 1);
        }}
      >
        <i class="fa-regular fa-thumbs-up"></i>
      </Compoment>
      {thumbup}
     <Space/>
      <span
        onClick={() => {
            setThumbDown(thumbdown + 1);
        }}
      >
        <i class="fa-regular fa-thumbs-down"></i>
      </span>
      {thumbdown}
      <Space/>
      <span
        onClick={() => {
            setMsg(msg + 1);
        }}
      >
        <i class="fa-regular fa-comment-dots"></i>
      </span>
      {msg}
      <Space/>
      <span
        onClick={() => {
            setStar(star + 1);
        }}
      >
        <i class="fa-regular fa-star"></i>
      </span>
      {star}
    </Wrapper>
  );
}

export default Likes;
