import React, { useState } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  width: 300px;
  height: 20px;
  flex-direction: row;

  padding-top: 0px;
  padding-right: 0px;
  padding-bottom: 15px;
  padding-left: 0px;

  margin: 0 auto;
  display: block;
 

  .upicon{
    color: #434343;
    font-size: 15px;
  }
  .downicon{
    color: #434343;
    font-size: 15px;
  }
  .cmicon{
    color: #434343;
    font-size: 15px;
  }
  .staricon{
    color: #434343;
    font-size: 15px;
  }
  .count{
    color: #434343;
    font-weight: 500;
    font-size: 15px;
    line-height: 16px;
    align-items: center;
    text-align: center;
  }

`;
const Space = styled.div`
  width: 10px;
  height: auto;
  display: inline-block;
`;

function Likes() {
  const [thumbup, setThumbUp] = useState(0); //좋아요 버튼구현
  const [thumbdown, setThumbDown] = useState(0); //좋아요 버튼구현
  const [msg, setMsg] = useState(0); //좋아요 버튼구현
  const [star, setStar] = useState(0); //좋아요 버튼구현

  return (
    <Wrapper>
      <span
        onClick={() => {
            setThumbUp(thumbup + 1);
        }}
      >
        <i className="upicon fa-regular fa-thumbs-up"></i>
      </span>
      <Space/>
      <i className="count">{thumbup}</i>
     <Space/>
     
      <span
        onClick={() => {
            setThumbDown(thumbdown + 1);
        }}
      >
        <i className="downicon fa-regular fa-thumbs-down"></i>
      </span>
      <Space/>
      <i className="count">{thumbdown}</i>
      <Space/>
      
      <span
        onClick={() => {
            setMsg(msg + 1);
        }}
      >
        <i className="cmicon fa-regular fa-comment-dots"></i>
      </span>
      <Space/>
      <i className="count">{msg}</i>
      <Space/>
      
      <span
        onClick={() => {
            setStar(star + 1);
        }}
      >
        <i className="staricon fa-regular fa-star"></i>
      </span>
      <Space/>
      <i className="count">{star}</i>
    </Wrapper>
  );
}

export default Likes;
