import React, { useState } from "react";
import styled from "styled-components";

const Wrapper = styled.div`
  width: 300px;
  height: 20px;

  padding-top: 10px;
  padding-right: 16px;
  padding-bottom: 30px;
  padding-left: 16px;
`;
const Label = styled.p`
    font-size: 16px;
    font-weight: 500;
    color: white;
`;

function Likes() {
  const [like, setLike] = useState(0); //좋아요 버튼구현

  return (
    <Wrapper>
        <Label>
            LIKE
            <span
          onClick={() => {
            setLike(like + 1);
          }}
        >
          <i class="fa-regular fa-heart"></i>
        </span>
        {like}
        </Label>
    </Wrapper>
  );
}

export default Likes;
