import React from "react";
import styled from "styled-components";
import HorizonLine from "../ui/HorizontalLine";

const Wrapper = styled.div`
  width: calc(100% - 32px);
  padding: 0px 16px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  border: transper;
  border-radius: 8px;
  cursor: pointer;
  background: #f9f9f9;
  :hover {
    background: lightgrey;
  }
`;
const Wrapper_2 = styled.div`
  display: flex;
  flex-direction: row;
  .usericon {
    padding-top: 10px;
    padding-botton: 10px;
    font-size: 30px;
    color: #d9d9d9;
  }
  .hearticon{
    color: #FA0050;
  }
`;
const ContentText = styled.p`
  font-size: 13px;
  white-space: pre-wrap;
  text-align: left;
  font-style: normal;
  font-weight: 600;
  line-height: 1.5;
`;

function CommentListItem(props) {
  const { comment } = props;
  const [heart, setHeart] = useState(0); //하트 버튼구현
  return (
    <Wrapper>
      <HorizonLine />
      <Wrapper_2>
        <i className="usericon fa-solid fa-circle-user"></i>
        <ContentText>{comment.content}</ContentText>
        <span
          onClick={() => {
            setHeart(heart + 1);
          }}
        >
          <i className="hearticon fa-solid fa-heart"></i>
        </span>
      </Wrapper_2>
    </Wrapper>
  );
}

export default CommentListItem;
